import 'package:ninja/ninja.dart';
import 'package:test/test.dart';

void main() {
  group('rsa', () {
    group('PublicKey', () {
      test('fromASN1', () {
        final key = RSAPublicKey.fromASN1(
            'MFwwDQYJKoZIhvcNAQEBBQADSwAwSAJBANsFjQ22G36v8jGhcOtCnWtkuqqI9mWOq5I6Gy4NBb46ch2+gXL8/sC37X2BGASnt2YymAm7xwS2+QE89YYvDdECAwEAAQ==');
        expect(key.n.toString(),
            '11471096350072129820579819100426547181616187185658504704996065334930805271197099322753245009150228584617512330162571541229290378786994493391408050685808081');
        expect(key.e.toString(), '65537');
      });
    });

    group('PrivateKey.fromASN1', () {
      test('pkcs#1', () {
        final key = RSAPrivateKey.fromASN1(
            '');
        expect(key.n.toString(),
            '');
        expect(key.e.toString(), '65537');
        expect(key.d.toString(),
            '');
        expect(key.p.toString(),
            '13311398800872123545509265659481938261907941937028495771208440340566859520917728290659412695924049986718341601969223777955429912069271339711686707239487713');
        expect(key.q.toString(),
            '12855853339879521453788356377992413936990740503119663040008894782605651726660339873651248325834950126916952352810922413550188676566583744808486210819115403');
      });
      test('pkcs#8', () {
        final key = RSAPrivateKey.fromASN1(
            '',
            fromPkcs1: false);
        print(key);

        expect(key.n.toString(),
            '11471096350072129820579819100426547181616187185658504704996065334930805271197099322753245009150228584617512330162571541229290378786994493391408050685808081');
        expect(key.e.toString(), '65537');
        expect(key.d.toString(),
            '9485704191461449311022822471710885422898632045097837923349508899724431403134778704337322169999541158279872714205624099708799374418241860212545272420550537');
        expect(key.p.toString(),
            '107414964168438393293800370153762414263170982299670223259057985688128607486167');
        expect(key.q.toString(),
            '106792349081681004195940644966825657043901438347969492098043946341032927758743');
      });
    });
  });
}
